#include <iostream>
#include <vector>
#include <set>
#include <algorithm>

using namespace std;

const int N = 1e5 + 5;

int n, m;
vector<int> g[N];
int par[N];
int depth[N];
int odd[N];
int ans = 0;

void dfs(int v, int p) {
    par[v] = p;
    depth[v] = depth[p] + 1;
    for (int u : g[v]) {
        if (u == p) continue;
        dfs(u, v);
    }
}

int lca(int u, int v) {
    while (depth[u] > depth[v]) {
        u = par[u];
    }
    while (depth[v] > depth[u]) {
        v = par[v];
    }
    if (u == v) return u;
    while (u != v) {
        u = par[u];
        v = par[v];
    }
    return u;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> m;
    for (int i = 0; i < m; i++) {
        int u, v;
        cin >> u >> v;
        g[u].push_back(v);
        g[v].push_back(u);
    }

    dfs(1, 0);

    for (int i = 0; i < m; i++) {
        int u, v;
        cin >> u >> v;
        int l = lca(u, v);
        if (l == u) {
            odd[u]++;
            odd[v]++;
        } else {
            odd[l]++;
        }
    }

    for (int i = 1; i <= n; i++) {
        if (odd[i] % 2) {
            cout << "NO\n";
            return 0;
        }
    }

    cout << "YES\n";

    return 0;
}
