public class Main {
    static int N, M;
    static int[] par;
    static int[] depth;
    static int[] cnt;
    static List<Integer>[] g;
    static int[] a;
    static int[] b;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        N = Integer.parseInt(st.nextToken());
        M = Integer.parseInt(st.nextToken());

        par = new int[N + 1];
        depth = new int[N + 1];
        cnt = new int[N + 1];
        g = new List[N + 1];
        for (int i = 0; i <= N; i++) {
            g[i] = new ArrayList<>();
        }
        a = new int[M];
        b = new int[M];

        for (int i = 0; i < M; i++) {
            st = new StringTokenizer(br.readLine());
            a[i] = Integer.parseInt(st.nextToken());
            b[i] = Integer.parseInt(st.nextToken());
            g[a[i]].add(b[i]);
            g[b[i]].add(a[i]);
        }

        for (int i = 1; i <= N; i++) {
            if (par[i] == 0) {
                dfs(i, 0);
            }
        }

        for (int i = 0; i < M; i++) {
            int x = a[i];
            int y = b[i];
            int d = depth[x] + depth[y] - 2 * depth[lca(x, y)];
            if (d % 2 == 1) {
                pw.println("NO");
                pw.close();
                return;
            }
            cnt[par[x]] += d / 2;
            cnt[par[y]] += d / 2;
            cnt[par[lca(x, y)]] -= d / 2;
        }

        for (int i = 1; i <= N; i++) {
            if (cnt[i] % 2 == 1) {
                pw.println("NO");
                pw.close();
                return;
            }
        }

        pw.println("YES");
        pw.close();
    }

    static int lca(int x, int y) {
        if (depth[x] < depth[y]) {
            int temp = x;
            x = y;
            y = temp;
        }
        while (depth[x] > depth[y]) {
            x = par[x];
        }
        while (x != y) {
            x = par[x];
            y = par[y];
        }
        return x;
    }

    static void dfs(int x, int p) {
        par[x] = p;
        depth[x] = depth[p] + 1;
        for (int y : g[x]) {
            if (y != p) {
                dfs(y, x);
            }
        }
    }
}
