#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define MAX_H 800
#define MAX_W 800
#define MAX_K (MAX_H * MAX_W)

int dx[] = {-1, 1, 0, 0};
int dy[] = {0, 0, -1, 1};

int bfs(int h, int w, int k, char grid[MAX_H][MAX_W + 1]) {
    int queue[MAX_H * MAX_W];
    int front = 0, rear = 0;
    int dist[MAX_H][MAX_W];
    int visited[MAX_H][MAX_W];
    int x, y, i, j, nx, ny, cnt;

    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            dist[i][j] = -1;
            visited[i][j] = 0;
        }
    }

    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            if (grid[i][j] == 'S') {
                dist[i][j] = 0;
                queue[rear++] = i * w + j;
                visited[i][j] = 1;
            }
        }
    }

    while (front < rear) {
        x = queue[front] / w;
        y = queue[front] % w;
        front++;

        for (i = 0; i < 4; i++) {
            nx = x + dx[i];
            ny = y + dy[i];

            if (nx >= 0 && nx < h && ny >= 0 && ny < w && grid[nx][ny] != '#') {
                if (dist[nx][ny] == -1) {
                    dist[nx][ny] = dist[x][y] + 1;
                    queue[rear++] = nx * w + ny;
                    visited[nx][ny] = 1;
                }
            }
        }

        cnt = 0;
        for (i = 0; i < 4; i++) {
            nx = x + dx[i];
            ny = y + dy[i];

            if (nx >= 0 && nx < h && ny >= 0 && ny < w && grid[nx][ny] == '#') {
                cnt++;
            }
        }

        if (cnt <= k) {
            for (i = 0; i < 4; i++) {
                nx = x + dx[i];
                ny = y + dy[i];

                if (nx >= 0 && nx < h && ny >= 0 && ny < w && grid[nx][ny] == '#') {
                    grid[nx][ny] = '.';
                    queue[rear++] = nx * w + ny;
                    visited[nx][ny] = 1;
                }
            }
        }
    }

    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            if (grid[i][j] == 'S' && visited[i][j] == 0) {
                return -1;
            }
        }
    }

    int max_dist = 0;
    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            if (grid[i][j] != '#' && dist[i][j] > max_dist) {
                max_dist = dist[i][j];
            }
        }
    }

    return max_dist;
}

int main() {
    int h, w, k;
    char grid[MAX_H][MAX_W + 1];
    int ans;

    scanf("%d %d %d", &h, &w, &k);
    for (int i = 0; i < h; i++) {
        scanf("%s", grid[i]);
    }

    ans = bfs(h, w, k, grid);
    if (ans == -1) {
        printf("-1\n");
    } else {
        printf("%d\n", ans);
    }

    return 0;
}
