using namespace std;

const int N = 810, M = 810;

int n, m, k, dis[N][N], vis[N][N];
char map[N][N];
vector<pair<int, int>> v[N][N];

void bfs(int x, int y) {
    queue<pair<int, int>> q;
    q.push({x, y});
    dis[x][y] = 0;
    vis[x][y] = 1;
    while (!q.empty()) {
        int x = q.front().first, y = q.front().second;
        q.pop();
        for (auto [nx, ny] : v[x][y]) {
            if (map[nx][ny] == '#' && dis[x][y] + 1 < dis[nx][ny]) {
                dis[nx][ny] = dis[x][y] + 1;
                q.push({nx, ny});
            } else if (map[nx][ny] == '.' && !vis[nx][ny]) {
                vis[nx][ny] = 1;
                q.push({nx, ny});
            }
        }
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    cin >> n >> m >> k;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            cin >> map[i][j];
            if (map[i][j] == 'S') {
                bfs(i, j);
            }
        }
    }
    int ans = 1e9;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            if (map[i][j] == '.' && i != 1 && j != 1 && i != n && j != m) {
                memset(dis, 0x3f, sizeof dis);
                memset(vis, 0, sizeof vis);
                for (int x = 1; x <= n; x++) {
                    for (int y = 1; y <= m; y++) {
                        v[x][y].clear();
                    }
                }
                for (int x = 1; x <= n; x++) {
                    for (int y = 1; y <= m; y++) {
                        if (map[x][y] == '.') {
                            for (int dx = -1; dx <= 1; dx++) {
                                for (int dy = -1; dy <= 1; dy++) {
                                    int nx = x + dx, ny = y + dy;
                                    if (nx >= 1 && nx <= n && ny >= 1 && ny <= m && (abs(dx) + abs(dy) <= k)) {
                                        v[x][y].push_back({nx, ny});
                                    }
                                }
                            }
                        }
                    }
                }
                bfs(i, j);
                for (int x = 1; x <= n; x++) {
                    for (int y = 1; y <= m; y++) {
                        if (map[x][y] == '.' && dis[x][y] < ans) {
                            ans = dis[x][y];
                        }
                    }
                }
            }
        }
    }
    cout << ans << '\n';
    return 0;
}
