from collections import deque

def bfs(x, y, k, h, w, grid):
    visited = [[False] * w for _ in range(h)]
    queue = deque([(x, y, 0, 0)])
    visited[x][y] = True
    while queue:
        x, y, step, lock = queue.popleft()
        if step > 0 and (x == 0 or x == h - 1 or y == 0 or y == w - 1):
            return step
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < h and 0 <= ny < w and not visited[nx][ny]:
                if grid[nx][ny] == '.':
                    queue.append((nx, ny, step + 1, lock))
                else:
                    queue.append((nx, ny, step, lock + 1))
        if lock < k:
            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                nx, ny = x + dx, y + dy
                if 0 <= nx < h and 0 <= ny < w and grid[nx][ny] == '#' and not visited[nx][ny]:
                    queue.append((nx, ny, step + 1, lock + 1))
    return -1

def solve():
    h, w, k = map(int, input().split())
    grid = [list(input().strip()) for _ in range(h)]
    for i in range(h):
        for j in range(w):
            if grid[i][j] == 'S':
                x, y = i, j
                break
    ans = bfs(x, y, k, h, w, grid)
    print(ans)

solve()
