    int i, v;
    d[u] = d[fa] + 1;
    c[u] = (d[u] & 1) ^ 1;
    for (i = a[u]; i; i = b[i]) {
        v = i ^ fa;
        if (!d[v]) {
            dfs(v, u);
            if (c[v]) {
                ans++;
            }
        }
    }
}

