    for (i = 1; i < n; i++) {
        scanf("%d %d", &x, &y);
        a[x] = a[y] = b[a[x]] = b[a[y]] = i;
    }
    dfs(1, 0);
    if (ans) {
        printf("First\n");
    } else {
        printf("Second\n");
    }
    return 0;
}
