int head[N], to[N << 1], nxt[N << 1], tot;
int fa[N], dfn[N], low[N], vis[N], scc[N], num, stk[N];
int dep[N], siz[N], top[N], son[N], dfc;

void add(int u, int v) {
    to[++tot] = v;
    nxt[tot] = head[u];
    head[u] = tot;
}

void tarjan(int u) {
    dfn[u] = low[u] = ++dfc;
    stk[++top[u]] = u;
    vis[u] = 1;
    for (int i = head[u]; i; i = nxt[i]) {
        int v = to[i];
        if (!dfn[v]) {
            tarjan(v);
            low[u] = (low[u] < low[v]) ? low[u] : low[v];
        } else if (vis[v]) {
            low[u] = (low[u] < dfn[v]) ? low[u] : dfn[v];
        }
    }
    if (dfn[u] == low[u]) {
        ++num;
        while (1) {
            int v = stk[top[u]];
            top[u]--, vis[v] = 0;
            scc[v] = num;
            if (v == u) break;
        }
    }
}

void dfs(int u, int f) {
    fa[u] = f;
    siz[u] = 1;
    for (int i = head[u]; i; i = nxt[i]) {
        int v = to[i];
        if (v == f) continue;
        dep[v] = dep[u] + 1;
        dfs(v, u);
        siz[u] += siz[v];
        if (siz[v] > siz[son[u]]) son[u] = v;
    }
}

int lca(int u, int v) {
    while (dep[u] != dep[v]) {
        if (dep[u] > dep[v]) u = fa[u];
        else v = fa[v];
    }
    return dep[u] < dep[v] ? u : v;
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i < n; i++) {
        int u, v;
        scanf("%d%d", &u, &v);
        add(u, v), add(v, u);
    }
    for (int i = 1; i <= n; i++) {
        if (!dfn[i]) tarjan(i);
    }
    for (int i = 1; i <= n; i++) {
        scanf("%d%d", &m, &cnt);
        if (scc[m] != scc[cnt]) {
            printf("YES\n");
            return 0;
        }
    }
    printf("NO\n");
    return 0;
}
