public class Main {
    static class Edge {
        int to, id;

        public Edge(int to, int id) {
            this.to = to;
            this.id = id;
        }
    }

    static class DSU {
        int[] fa;

        public DSU(int n) {
            fa = new int[n + 1];
            for (int i = 1; i <= n; i++) fa[i] = i;
        }

        int find(int x) {
            if (fa[x] != x) fa[x] = find(fa[x]);
            return fa[x];
        }

        void merge(int x, int y) {
            x = find(x);
            y = find(y);
            fa[x] = y;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter out = new PrintWriter(System.out);
        int n = Integer.parseInt(in.readLine());
        List<Edge>[] g = new List[n + 1];
        List<Edge>[] g2 = new List[n + 1];
        for (int i = 1; i <= n; i++) {
            g[i] = new ArrayList<>();
            g2[i] = new ArrayList<>();
        }
        StringTokenizer st;
        for (int i = 1; i < n; i++) {
            st = new StringTokenizer(in.readLine());
            int u = Integer.parseInt(st.nextToken());
            int v = Integer.parseInt(st.nextToken());
            g[u].add(new Edge(v, i));
            g[v].add(new Edge(u, i));
        }
        for (int i = 1; i <= n; i++) {
            st = new StringTokenizer(in.readLine());
            int u = Integer.parseInt(st.nextToken());
            int v = Integer.parseInt(st.nextToken());
            g2[u].add(new Edge(v, i));
            g2[v].add(new Edge(u, i));
        }
        DSU dsu = new DSU(n);
        for (int i = 1; i <= n; i++) {
            for (Edge e : g[i]) {
                dsu.merge(e.to, i);
            }
        }
        boolean[] vis = new boolean[n + 1];
        for (int i = 1; i <= n; i++) {
            if (!vis[i]) {
                vis[i] = true;
                int x = i;
                int y = i;
                for (Edge e : g2[i]) {
                    if (dsu.find(e.to) == dsu.find(x)) {
                        y = e.to;
                    }
                }
                int cnt = 0;
                while (x != y) {
                    vis[x] = true;
                    for (Edge e : g2[x]) {
                        if (e.to == y) {
                            cnt++;
                        }
                    }
                    x = dsu.find(x);
                }
                if (cnt != 1) {
                    out.println("NO");
                    out.close();
                    return;
                }
            }
        }
        out.println("YES");
        out.close();
    }
}
