int knapsack(int n, int w, vector<int>& wv) {
    vector<vector<int>> dp(n + 1, vector<int>(w + 1, 0));
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= w; j++) {
            if (wv[i - 1] <= j) {
                dp[i][j] = max(dp[i - 1][j], dp[i - 1][j - wv[i - 1]] + wv[i - 1]);
            } else {
                dp[i][j] = dp[i - 1][j];
            }
        }
    }
    return dp[n][w];
