        for (int j = 1; j <= w; j++) {
            if (w_[i - 1] > j) {
                dp[j][i] = dp[j][i - 1];
            } else {
                dp[j][i] = (v_[i - 1] + dp[j - w_[i - 1]][i - 1] > dp[j][i - 1]) ? (v_[i - 1] + dp[j - w_[i - 1]][i - 1]) : dp[j][i - 1];
            }
        }
    }

    return dp[w][n];
}

int main() {
    int n, w;
    scanf("%d %d", &n, &w);

    int w_[N], v_[N];

    for (int i = 0; i < n; i++) {
        scanf("%d %d", &w_[i], &v_[i]);
    }

    printf("%d\n", knapsack(n, w, w_, v_));

    return 0;
}
