import java.util.*;
import java.io.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }

        double nextDouble() {
            return Double.parseDouble(next());
        }

        String nextLine() {
            String str = "";
            try {
                str = br.readLine();
            } catch (IOException e) {
                e.printStackTrace();
            }
            return str;
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        int n = sc.nextInt();
        int q = sc.nextInt();
        int a = sc.nextInt();
        int b = sc.nextInt();
        int[] x = new int[q];
        for (int i = 0; i < q; i++) {
            x[i] = sc.nextInt();
        }
        Arrays.sort(x);
        int ans = 0;
        int i = 0;
        int j = q - 1;
        while (i < j) {
            if (x[i] < a && x[j] < b) {
                ans += Math.abs(x[i] - a) + Math.abs(x[j] - b);
                i++;
                j--;
            } else if (x[i] < a && x[j] >= b) {
                ans += Math.abs(x[i] - a) + Math.abs(x[j] - a);
                i++;
            } else if (x[i] >= a && x[j] < b) {
                ans += Math.abs(x[j] - b) + Math.abs(x[i] - b);
                j--;
            } else {
                ans += Math.abs(x[i] - x[j]);
                i++;
                j--;
            }
        }
        if (i == j) {
            ans += Math.abs(x[i] - a);
        }
        System.out.println(ans);
    }
}
