typedef struct {
    int x, w, t;
    int next;
} Ant;

int cmp(const void *a, const void *b) {
    Ant *x = (Ant *)a;
    Ant *y = (Ant *)b;
    return x->x - y->x;
}

int main() {
    int n, L, T;
    scanf("%d %d %d", &n, &L, &T);

    Ant *ants = (Ant *)malloc(n * sizeof(Ant));
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &ants[i].x, &ants[i].w);
        ants[i].t = 0;
        ants[i].next = (i + 1) % n;
    }

    qsort(ants, n, sizeof(Ant), cmp);

    int i = 0;
    while (T > 0) {
        int t = 0;
        while (i < n && ants[i].t + 1 <= T) {
            ants[i].t += 1;
            ants[i].x = (ants[i].x + (ants[i].w == 1 ? 1 : -1)) % L;
            if (ants[i].x < 0) ants[i].x += L;
            if (ants[i].x == ants[ants[i].next].x) {
                ants[i].w = 3 - ants[i].w;
                ants[ants[i].next].w = 3 - ants[ants[i].next].w;
                t = 1;
            }
            i = (i + 1) % n;
        }
        if (t == 0) break;
        T -= t;
    }

    for (int i = 0; i < n; i++) {
        printf("%d\n", ants[i].x);
    }

    free(ants);
    return 0;
}
