public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }
    }

    public static void main(String[] args) throws IOException {
        FastReader sc = new FastReader();
        PrintWriter out = new PrintWriter(System.out);
        int n = sc.nextInt();
        long L = sc.nextLong();
        long T = sc.nextLong();
        long[] X = new long[n];
        int[] W = new int[n];
        for (int i = 0; i < n; i++) {
            X[i] = sc.nextLong();
            W[i] = sc.nextInt();
        }
        long[] ans = new long[n];
        for (int i = 0; i < n; i++) {
            long x = X[i];
            int w = W[i];
            long t = T;
            while (t > 0) {
                if (x + t > L) {
                    x = (x + t) % L;
                    t = 0;
                } else {
                    t -= (x + t) - x;
                    x += t;
                }
                for (int j = 0; j < i; j++) {
                    long y = X[j];
                    int ww = W[j];
                    if (x == y) {
                        if (w == 1) {
                            w = 2;
                        } else {
                            w = 1;
                        }
                        if (ww == 1) {
                            ww = 2;
                        } else {
                            ww = 1;
                        }
                        if (w == 1) {
                            x = (x + L - y) % L;
                        } else {
                            x = (x + y) % L;
                        }
                        if (ww == 1) {
                            X[j] = (X[j] + L - y) % L;
                        } else {
                            X[j] = (X[j] + y) % L;
                        }
                        t = 0;
                        break;
                    }
                }
            }
            ans[i] = x;
        }
        for (int i = 0; i < n; i++) {
            out.println(ans[i]);
        }
        out.close();
    }
