const int MOD = 1e9 + 7;

int main() {
    int n, m;
    cin >> n >> m;

    vector<vector<int>> dp(n + 1, vector<int>(m + 1));
    dp[0][0] = 1;

    for (int i = 0; i < n; i++) {
        for (int j = 0; j <= m; j++) {
            if (i + j <= m) {
                dp[i + j + 1][j] = (dp[i + j + 1][j] + dp[i][j]) % MOD;
            }
            if (j) {
                dp[i + j - 1][j - 1] = (dp[i + j - 1][j - 1] + dp[i][j] * j) % MOD;
            }
        }
    }

    int ans = 0;
    for (int i = 0; i <= m; i++) {
        ans = (ans + dp[n][i] * C(m, i)) % MOD;
    }

    cout << ans << endl;

    return 0;
}

int C(int n, int k) {
    vector<int> fact(n + 1);
    fact[0] = 1;
    for (int i = 1; i <= n; i++) {
        fact[i] = (fact[i - 1] * i) % MOD;
    }

    return (fact[n] * modInverse(fact[k], MOD) % MOD * modInverse(fact[n - k], MOD) % MOD) % MOD;
}

int modInverse(int a, int m) {
    int m0 = m;
    int y = 0, x = 1;

    if (m == 1) {
        return 0;
    }

    while (a > 1) {
        int q = a / m;
        int t = m;

        m = a % m;
        a = t;
        t = y;

        y = x - q * y;
        x = t;
    }

    if (x < 0) {
        x += m0;
    }

    return x;
}
