        for (int i = 0; i < m; i++) {
            long g = x[i];
            for (int j = i + 1; j < m; j++) {
                g = gcd(g, x[j] - x[i]);
            }
            long cnt = (n - x[m - 1]) / g;
            ans = (ans * pow(g, cnt)) % MOD;
        }

        pw.println(ans);
        pw.close();
    }

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = res * a % MOD;
            }
            a = a * a % MOD;
            b >>= 1;
        }
        return res;
    }

    static long gcd(long a, long b) {
        return b == 0 ? a : gcd(b, a % b);
    }
