    for (int i = 1; i <= q; i++) {
        int x = (a[i] - 1) / m * n + (a[i] - 1) % m / 7 + 1;
        int y = (a[i] - 1) % m % 7 + 1;
        if (!visited[x][y]) {
            count++;
            int dx[] = {-1, 0, 1, 0};
            int dy[] = {0, -1, 0, 1};
            for (int j = 0; j < 4; j++) {
                int nx = x + dx[j];
                int ny = y + dy[j];
                if (nx >= 1 && nx <= n && ny >= 1 && ny <= 7 && !visited[nx][ny]) {
                    while (nx != x || ny != y) {
                        visited[nx][ny] = 1;
                        nx += dx[j];
                        ny += dy[j];
                    }
                }
            }
        }
    }

    printf("%d\n", count);

    return 0;
