public class Main {
    static final int N = 100000;
    static final int M = 100000;
    static final int Q = 100000;
    static final int MOD = 1000000007;

    static int n, m, q;
    static int[] a = new int[Q + 1];

    static int[][] ds = new int[N + 1][7];
    static int[] sz = new int[N + 1];

    static int find(int i) {
        return ds[i][0] < 0 ? i : (ds[i][0] = find(ds[i][0]));
    }

    static void join(int i, int j) {
        i = find(i);
        j = find(j);
        if (i == j) return;
        if (ds[i][0] < ds[j][0]) {
            ds[j][0] = i;
            sz[i] += sz[j];
        } else {
            if (ds[i][0] == ds[j][0]) ds[j][0]--;
            ds[i][0] = j;
            sz[j] += sz[i];
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        n = Integer.parseInt(st.nextToken());
        m = Integer.parseInt(st.nextToken());
        q = Integer.parseInt(st.nextToken());
        st = new StringTokenizer(br.readLine());
        for (int i = 1; i <= q; i++) {
            a[i] = Integer.parseInt(st.nextToken());
        }
        for (int i = 0; i <= N; i++) {
            for (int j = 0; j < 7; j++) {
                ds[i][j] = -1;
                sz[i] = 1;
            }
        }
        for (int i = 1; i <= q; i++) {
            int x = a[i] % m;
            int y = (7 * n + x - 8) % m;
            for (int j = 0; j < 7; j++) {
                if (y + j >= m) y += j - m;
                else y += j;
                join(x, y);
            }
        }
        int ans = 0;
        for (int i = 0; i <= N; i++) {
            if (ds[i][0] < 0) ans++;
        }
        System.out.println(ans);
    }
}
