#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007

int max(int a, int b) {
    return a > b ? a : b;
}

int min(int a, int b) {
    return a < b ? a : b;
}

int dp[3001][3001];

int solve(int i, int j) {
    if (i == j) return 1;
    if (dp[i][j] != -1) return dp[i][j];
    int ans = 0;
    for (int k = i; k < j; k++) {
        ans = (ans + solve(i, k) * solve(k + 1, j)) % MOD;
    }
    return dp[i][j] = ans;
}

int main() {
    int n, k, x;
    scanf("%d %d %d", &n, &k, &x);
    int s[k + 1];
    for (int i = 0; i <= k; i++) {
        scanf("%d", &s[i]);
    }
    int m = 0;
    for (int i = 0; i < k; i++) {
        m = max(m, s[i + 1] - s[i]);
    }
    memset(dp, -1, sizeof(dp));
    int ans = solve(0, m);
    for (int i = 0; i <= m; i++) {
        ans = (ans - solve(0, i)) % MOD;
    }
    ans = (ans + MOD) % MOD;
    printf("%d\n", (1 << (n - 1)) - ans);
    return 0;
}
