    static long solve(int i, int time) {
        if (i == k + 1) {
            return time == x ? 1 : 0;
        }
        if (dp[i][time] != -1) {
            return dp[i][time];
        }
        long ans = 0;
        for (int j = i; j <= k; j++) {
            if (s[j] - s[i] <= 300) {
                ans += solve(j + 1, time + s[j] - s[i] + 1);
                ans %= MOD;
            }
        }
        return dp[i][time] = ans;
    }
