            if (dis[x][y] > dis[u.x][u.y] + 1) {
                dis[x][y] = dis[u.x][u.y] + 1;
                q.push({x, y, dis[x][y]});
            }
        }
    }
    if (dis[gx][gy] == 0x3f3f3f3f) {
        cout << -1 << endl;
        return 0;
    }
    for (int i = 1; i <= k; i++) {
        for (int j = 0; j < 4; j++) {
            int x = a[i], y = b[i];
            if (dis[x][y] == dis[x + dx[j]][y + dy[j]]) {
                g[i] = j;
