    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            for (int k = 0; k < 4; k++) {
                dis[i][j] = min(dis[i][j], dis[i + dx[k]][j + dy[k]]);
            }
        }
    }
    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            for (int k = 0; k < 4; k++) {
                dis[i][j] = min(dis[i][j], dis[i + dx[k]][j + dy[k]]);
            }
        }
    }
    long long ans = 0;
    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            for (int k = 0; k < 4; k++) {
                if (dis[i][j] == dis[i + dx[k]][j + dy[k]]) {
                    for (int x : v[i]) {
                        if (c[x] == k) {
