    static class Pair implements Comparable<Pair> {
        int x, y, dir, cost;

        Pair(int x, int y, int dir, int cost) {
            this.x = x;
            this.y = y;
            this.dir = dir;
            this.cost = cost;
        }

        public int compareTo(Pair other) {
            return Integer.compare(this.cost, other.cost);
        }
    }

    static class Edge {
        int to, cost;

        Edge(int to, int cost) {
            this.to = to;
            this.cost = cost;
        }
    }

    static final int INF = (int) 1e9;
    static final int MAXN = (int) 1e5 + 5;

    static int n, m, k, f;
    static int[][] g = new int[MAXN][4];
    static int[][] d = new int[MAXN][4];
    static int[][] dis = new int[MAXN][MAXN];
    static int[][] pre = new int[MAXN][MAXN];
    static int[][][] dp = new int[MAXN][MAXN][4];
    static int[][][] next = new int[MAXN][MAXN][4];
    static int[] dx = {0, 0, 1, -1};
    static int[] dy = {1, -1, 0, 0};
    static List<Edge>[] e = new List[MAXN];

    static void dijkstra(int sx, int sy) {
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                dis[i][j] = INF;
                pre[i][j] = -1;
            }
        }
        dis[sx][sy] = 0;
        PriorityQueue<Pair> q = new PriorityQueue<>();
        q.add(new Pair(sx, sy, 0, 0));
        while (!q.isEmpty()) {
            Pair p = q.poll();
            int x = p.x, y = p.y, dir = p.dir, cost = p.cost;
            if (dis[x][y] < cost) continue;
            for (int i = 0; i < 4; i++) {
                int nx = x + dx[i], ny = y + dy[i];
                if (nx >= 1 && nx <= n && ny >= 1 && ny <= m) {
                    int ndir = (dir + i) % 4;
                    if (g[nx][ny] == 1 && d[nx][ny] == ndir) {
                        int ncost = cost + 1;
                        if (ncost < dis[nx][ny]) {
                            dis[nx][ny] = ncost;
                            pre[nx][ny] = x;
                            q.add(new Pair(nx, ny, ndir, ncost));
                        }
                    } else if (g[nx][ny] == 0) {
                        int ncost = cost;
                        if (ncost < dis[nx][ny]) {
                            dis[nx][ny] = ncost;
                            pre[nx][ny] = x;
                            q.add(new Pair(nx, ny, dir, ncost));
                        }
                    }
                }
            }
        }
    }

    static void solve(int sx, int sy, int gx, int gy) {
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                for (int k = 0; k < 4; k++) {
                    dp[i][j][k] = INF;
                    next[i][j][k] = -1;
                }
            }
        }
        dp[sx][sy][0] = 0;
        for (int i = 0; i < 4; i++) {
            next[sx][sy][i] = 0;
        }
        PriorityQueue<Pair> q = new PriorityQueue<>();
        q.add(new Pair(sx, sy, 0, 0));
        while (!q.isEmpty()) {
            Pair p = q.poll();
            int x = p.x, y = p.y, dir = p.dir, cost = p.cost;
            if (dp[x][y][dir] < cost) continue;
            for (int i = 0; i < 4; i++) {
                int nx = x + dx[i], ny = y + dy[i];
                if (nx >= 1 && nx <= n && ny >= 1 && ny <= m) {
                    int ndir = (dir + i) % 4;
                    int ncost = cost + e[nx][ny].cost;
                    if (ncost < dp[nx][ny][ndir]) {
                        dp[nx][ny][ndir] = ncost;
                        next[nx][ny][ndir] = i;
                        q.add(new Pair(nx, ny, ndir, ncost));
                    }
                }
            }
        }
        int ans = INF;
        for (int i = 0; i < 4; i++) {
            int nx = gx, ny = gy;
            int ncost = 0;
            while (nx != sx || ny != sy) {
                int x = pre[nx][ny];
                int y = pre[nx][ny];
                int dir = next[nx][ny][i];
                ncost += e[nx][ny].cost;
                nx = x - dx[dir];
                ny = y - dy[dir];
            }
            ans = Math.min(ans, dp[gx][gy][i] + ncost);
        }
        if (ans == INF) ans = -1;
        System.out.println(ans);
    }

    public static void main(String[] args) {
        FastScanner sc = new FastScanner();
        PrintWriter out = new PrintWriter(System.out);
        n = sc.nextInt();
        m = sc.nextInt();
        k = sc.nextInt();
        f = sc.nextInt();
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                g[i][j] = sc.nextInt();
            }
        }
        int sx = sc.nextInt();
