        int gx = sc.nextInt();
        int gy = sc.nextInt();
        for (int i = 1; i <= k; i++) {
            int x = sc.nextInt();
            int y = sc.nextInt();
            int dir = sc.nextInt();
            int d = sc.nextInt();
            int e = sc.nextInt();
            d[x][y] = dir;
            e[x][y] = new Edge(x, e);
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                if (g[i][j] == 1) {
                    for (int k = 0; k < 4; k++) {
                        int nx = i + dx[k];
                        int ny = j + dy[k];
                        if (nx >= 1 && nx <= n && ny >= 1 && ny <= m) {
                            int ndir = (d[i][j] + k) % 4;
                            if (d[nx][ny] == ndir) {
                                e[i][j] = new Edge(i, f * Math.abs(d[i][j] - ndir));
                            }
                        }
                    }
                }
            }
        }
        dijkstra(sx, sy);
        solve(sx, sy, gx, gy);
        out.close();
    }
}
