#include <stdio.h>
#include <stdlib.h>

#define N 400000
#define Q 50000

typedef struct Node {
    int p, a, sum, lazy;
    struct Node* left, *right;
} Node;

Node* newNode(int p, int a) {
    Node* node = (Node*)malloc(sizeof(Node));
    node->p = p;
    node->a = a;
    node->sum = a;
    node->lazy = 0;
    node->left = node->right = NULL;
    return node;
}

void push(Node* node) {
    if (node->lazy) {
        node->sum += node->lazy * (1 << (node->right ? 1 : 0));
        if (node->left) {
            node->left->lazy += node->lazy;
            node->right->lazy += node->lazy;
        }
        node->lazy = 0;
    }
}

void pull(Node* node) {
    if (node->left && node->right) {
        node->sum = node->left->sum + node->right->sum;
    }
}

void update(Node* node, int l, int r, int x, int y, int z) {
    if (l > y || r < x) return;
    if (l >= x && r <= y) {
        node->lazy += z;
        return;
    }
    push(node);
    int mid = (l + r) >> 1;
    if (node->left) {
        update(node->left, l, mid, x, y, z);
    }
    if (node->right) {
        update(node->right, mid + 1, r, x, y, z);
    }
    pull(node);
}

int query(Node* node, int l, int r, int x, int y) {
    if (l > y || r < x) return 0;
    if (l >= x && r <= y) {
        return node->sum;
    }
    push(node);
    int mid = (l + r) >> 1;
    int res = 0;
    if (node->left) {
        res += query(node->left, l, mid, x, y);
    }
    if (node->right) {
        res += query(node->right, mid + 1, r, x, y);
    }
    pull(node);
    return res;
}

void insert(Node** root, int p, int a) {
    if (*root == NULL) {
        *root = newNode(p, a);
        return;
    }
    Node* node = *root;
    while (1) {
        push(node);
        if (node->p == p) {
            node->a = a;
            node->sum = a;
            return;
        }
        if (node->p > p) {
            if (node->left) {
                node = node->left;
            } else {
                node->left = newNode(p, a);
                return;
            }
        } else {
            if (node->right) {
                node = node->right;
            } else {
                node->right = newNode(p, a);
                return;
            }
        }
    }
}

void solve() {
    int n, q;
    scanf("%d %d", &n, &q);
    Node* root = NULL;
    for (int i = 0; i < n; i++) {
        int p, a;
        scanf("%d %d", &p, &a);
        insert(&root, p, a);
    }
    while (q--) {
        int type;
        scanf("%d", &type);
        if (type == 1) {
            int v, d, x;
            scanf("%d %d %d", &v, &d, &x);
            update(root, 0, n - 1, v, v + d - 1, x);
        } else if (type == 2) {
            int v, d;
            scanf("%d %d", &v, &d);
            printf("%d\n", query(root, 0, n - 1, v, v + d - 1));
        } else {
            int pr, ar;
            scanf("%d %d", &pr, &ar);
            insert(&root, pr, ar);
        }
    }
}

int main() {
    solve();
    return 0;
}
