struct Node {
    long long sum, lazy;
};

Node tree[N];

void push(int node, int l, int r) {
    tree[node].sum = (r - l + 1) * tree[node].lazy;
    if (l != r) {
        tree[node * 2].lazy += tree[node].lazy;
        tree[node * 2 + 1].lazy += tree[node].lazy;
    }
    tree[node].lazy = 0;
}

void update(int node, int l, int r, int x, int y, long long val) {
    if (x <= l && r <= y) {
        tree[node].sum = (r - l + 1) * val;
        tree[node].lazy = val;
        return;
    }
    int mid = (l + r) / 2;
    push(node, l, r);
    if (x <= mid) update(node * 2, l, mid, x, y, val);
    if (y > mid) update(node * 2 + 1, mid + 1, r, x, y, val);
    tree[node].sum = tree[node * 2].sum + tree[node * 2 + 1].sum;
}

long long query(int node, int l, int r, int x, int y) {
    if (x <= l && r <= y) return tree[node].sum;
    int mid = (l + r) / 2;
    push(node, l, r);
    long long res = 0;
    if (x <= mid) res += query(node * 2, l, mid, x, y);
    if (y > mid) res += query(node * 2 + 1, mid + 1, r, x, y);
    return res;
}

