class SegmentTree:
    def __init__(self, n):
        self.n = n
        self.tree = [0] * (4 * n)
        self.lazy = [0] * (4 * n)

    def build(self, node, start, end):
        if start == end:
            self.tree[node] = 0
            return
        mid = (start + end) // 2
        self.build(2 * node + 1, start, mid)
        self.build(2 * node + 2, mid + 1, end)
        self.tree[node] = self.tree[2 * node + 1] + self.tree[2 * node + 2]

    def push(self, node, start, end):
        if self.lazy[node] != 0:
            self.tree[node] += (end - start + 1) * self.lazy[node]
            if start != end:
                self.lazy[2 * node + 1] += self.lazy[node]
                self.lazy[2 * node + 2] += self.lazy[node]
            self.lazy[node] = 0

    def update(self, node, start, end, left, right, val):
        self.push(node, start, end)
        if left > end or right < start:
            return
        if left <= start and end <= right:
            self.lazy[node] += val
            self.push(node, start, end)
            return
        mid = (start + end) // 2
        self.update(2 * node + 1, start, mid, left, right, val)
        self.update(2 * node + 2, mid + 1, end, left, right, val)
        self.tree[node] = self.tree[2 * node + 1] + self.tree[2 * node + 2]

    def query(self, node, start, end, left, right):
        self.push(node, start, end)
        if left > end or right < start:
            return 0
        if left <= start and end <= right:
            return self.tree[node]
        mid = (start + end) // 2
        return self.query(2 * node + 1, start, mid, left, right) + self.query(2 * node + 2, mid + 1, end, left, right)


class Tree:
    def __init__(self, n):
        self.n = n
        self.parent = [-1] * n
        self.salary = [0] * n
        self.upper = [[] for _ in range(n)]
        self.lower = [[] for _ in range(n)]
        self.children = [[] for _ in range(n)]

    def build(self, i, p):
        self.parent[i] = p
        self.children[p].append(i)
        for j in self.children[i]:
            self.upper[i].append(len(self.upper[j]))
            self.lower[j].append(len(self.lower[i]))

    def dfs(self, i):
        for j in self.children[i]:
            self.dfs(j)
        self.build(i, self.parent[i])


class Query:
    def __init__(self, type, v, d, x=None, pr=None, ar=None):
        self.type = type
        self.v = v
        self.d = d
        self.x = x
        self.pr = pr
        self.ar = ar


def solve():
    n, q = map(int, input().split())
    tree = Tree(n)
    for i in range(n):
        p, a = map(int, input().split())
        tree.salary[i] = a
        tree.build(i, p)
    queries = []
    for _ in range(q):
        query = Query(*map(int, input().split()))
        queries.append(query)
    segment_tree = SegmentTree(n)
    segment_tree.build(0, 0, n - 1)
    for query in queries:
        if query.type == 1:
            segment_tree.update(0, 0, n - 1, query.v, query.v, query.x)
            for i in range(len(tree.lower[query.v])):
                segment_tree.update(0, 0, n - 1, tree.lower[query.v][i], tree.lower[query.v][i], query.x)
        elif query.type == 2:
            ans = tree.salary[query.v]
            for i in range(len(tree.lower[query.v])):
                ans += segment_tree.query(0, 0, n - 1, tree.lower[query.v][i], tree.lower[query.v][i])
            print(ans)
        else:
            tree.n += 1
            tree.parent.append(query.pr)
            tree.salary.append(query.ar)
            tree.build(tree.n - 1, query.pr)
            segment_tree.build(0, 0, tree.n - 1)


solve()
