public class Main {
    static class Node {
        long sum;
        long lazy;

        public Node() {
            sum = 0;
            lazy = 0;
        }
    }

    static class SegmentTree {
        int n;
        Node[] tree;

        public SegmentTree(int n) {
            this.n = n;
            tree = new Node[4 * n];
            for (int i = 0; i < 4 * n; i++) {
                tree[i] = new Node();
            }
        }

        void push(int v, int l, int r) {
            tree[v].sum += (r - l) * tree[v].lazy;
            if (l != r) {
                tree[v * 2 + 1].lazy += tree[v].lazy;
                tree[v * 2 + 2].lazy += tree[v].lazy;
            }
            tree[v].lazy = 0;
        }

        void update(int v, int l, int r, int x, int y, long val) {
            push(v, l, r);
            if (x <= l && r <= y) {
                tree[v].lazy += val;
                push(v, l, r);
            } else if (x < r && l < y) {
                int m = (l + r) / 2;
                update(v * 2 + 1, l, m, x, y, val);
                update(v * 2 + 2, m, r, x, y, val);
                tree[v].sum = tree[v * 2 + 1].sum + tree[v * 2 + 2].sum;
            }
        }

        long query(int v, int l, int r, int x, int y) {
            push(v, l, r);
            if (x <= l && r <= y) {
                return tree[v].sum;
            } else if (x < r && l < y) {
                int m = (l + r) / 2;
                return query(v * 2 + 1, l, m, x, y) + query(v * 2 + 2, m, r, x, y);
            } else {
                return 0;
            }
        }
    }

    static int[] p;
    static long[] a;
    static SegmentTree tree;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int q = Integer.parseInt(st.nextToken());
        p = new int[n];
        a = new long[n];
        tree = new SegmentTree(n);
        for (int i = 0; i < n; i++) {
            st = new StringTokenizer(br.readLine());
            p[i] = Integer.parseInt(st.nextToken());
            a[i] = Long.parseLong(st.nextToken());
            tree.update(0, 0, n, i, i + 1, a[i]);
        }
        for (int i = 0; i < q; i++) {
            st = new StringTokenizer(br.readLine());
            int type = Integer.parseInt(st.nextToken());
            if (type == 1) {
                int v = Integer.parseInt(st.nextToken());
                int d = Integer.parseInt(st.nextToken());
                int x = Integer.parseInt(st.nextToken());
                tree.update(0, 0, n, v, v + d + 1, x);
            } else if (type == 2) {
                int v = Integer.parseInt(st.nextToken());
                int d = Integer.parseInt(st.nextToken());
                System.out.print(tree.query(0, 0, n, v, v + d + 1) + " ");
            } else {
                int pr = Integer.parseInt(st.nextToken());
                int ar = Integer.parseInt(st.nextToken());
                int c = Integer.parseInt(st.nextToken());
                p[c - 1] = pr;
                a[c - 1] = ar;
                tree.update(0, 0, n, c - 1, c, ar);
            }
        }
    }
}
