#define N 200
#define INF 1000000000

int n;
int d[N][N];
int p[N][N];
int q[N];
int ans[N];

void dfs(int u, int fa) {
    for (int v = 0; v < n; v++) {
        if (v != fa && d[u][v] == 1) {
            p[u][v] = p[v][u] = 1;
            dfs(v, u);
        }
    }
}

int main() {
    int m;
    scanf("%d", &n);
    m = n - 1;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            d[i][j] = 0;
        }
    }
    for (int i = 0; i < m; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        u--; v--;
        d[u][v] = d[v][u] = 1;
    }
    for (int i = 0; i < n; i++) {
        q[i] = i;
    }
    for (int i = 0; i < n; i++) {
        int u = q[i];
        for (int j = i + 1; j < n; j++) {
            int v = q[j];
            if (d[u][v] == 1) {
                q[j] = u;
            }
        }
    }
    for (int i = 0; i < n; i++) {
        int u = q[i];
        for (int j = i + 1; j < n; j++) {
            int v = q[j];
            if (d[u][v] == 1) {
                dfs(u, v);
                break;
            }
        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (p[i][j] == 1) {
                ans[i] = j;
                break;
            }
        }
    }
    printf("! ");
    for (int i = 0; i < n; i++) {
        printf("(%d,%d) ", i, ans[i]);
    }
    printf("\n");
    return 0;
}
