const int N = 200;

int n;
vector<int> g[N];
int d[N];
int q[N];
int ans[N];
int cnt;

void dfs(int v, int p) {
    for (int i = 0; i < g[v].size(); i++) {
        int to = g[v][i];
        if (to == p) continue;
        dfs(to, v);
        d[v] = max(d[v], d[to] + 1);
    }
}

int main() {
    cin >> n;
    for (int i = 1; i < n; i++) {
        int u, v;
        cin >> u >> v;
        u--; v--;
        g[u].push_back(v);
        g[v].push_back(u);
    }

    dfs(0, -1);
    int mx = 0;
    for (int i = 0; i < n; i++) {
        mx = max(mx, d[i]);
    }

    for (int i = 0; i < n; i++) {
        if (d[i] == mx) {
            q[cnt++] = i;
        }
    }

    string s;
    for (int i = 0; i < n; i++) {
        s.push_back('0');
    }

    for (int i = 0; i < cnt; i++) {
        s[q[i]] = '1';
        int x;
        cin >> x;
        if (x == 0) {
            for (int j = 0; j < n; j++) {
                if (s[j] == '1') {
                    for (int k = 0; k < g[j].size(); k++) {
                        int to = g[j][k];
                        if (s[to] == '0') {
                            ans[g[j][k]] = 1;
                            ans[g[j][k] ^ 1] = 1;
                        }
                    }
                }
            }
        }
    }

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < g[i].size(); j++) {
            int to = g[i][j];
            if (ans[to] == 0) {
                cout << "(" << i << "," << to << ") ";
            }
        }
    }
    cout << endl;

    return 0;
}
