bool vis[26];
int cnt;

void dfs(int x) {
    if (x == cnt) {
        int len = 0;
        for (int i = 0; i < n; i++) {
            bool ok = true;
            for (int j = 0; j < strlen(s[i]); j++) {
                if (!vis[s[i][j] - 'a']) {
                    ok = false;
                    break;
                }
            }
            if (ok) len++;
        }
        if (len == n) {
            bool flag = true;
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < strlen(s[i]); j++) {
                    if (vis[s[i][j] - 'a']) {
                        flag = false;
                        break;
                    }
                }
                if (!flag) break;
            }
            if (flag) {
                int max_len = 0;
                for (int i = 0; i < n; i++) {
                    int len = 0;
                    for (int j = 0; j < strlen(s[i]); j++) {
                        if (vis[s[i][j] - 'a']) len++;
                    }
                    max_len = max_len > len ? max_len : len;
                }
                if (max_len == cnt) {
                    char res[1000];
                    int p = 0;
                    for (int i = 0; i < n; i++) {
                        for (int j = 0; j < strlen(s[i]); j++) {
                            if (vis[s[i][j] - 'a']) {
                                res[p++] = s[i][j];
                            }
                        }
                    }
                    res[p] = '\0';
                    printf("%s\n", res);
                    exit(0);
                }
            }
        }
        return;
    }
    vis[x] = true;
    dfs(x + 1);
    vis[x] = false;
    dfs(x + 1);
}

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%s", s[i]);
    }
    for (int i = 0; i < 26; i++) {
        vis[i] = true;
        dfs(i + 1);
        vis[i] = false;
    }
    printf("\n");
    return 0;
}
