import java.util.*;
import java.io.*;

public class Main {
    static class TrieNode {
        TrieNode[] children = new TrieNode[26];
        boolean endOfWord;
    }

    static TrieNode root = new TrieNode();

    static void insert(String s) {
        TrieNode node = root;
        for (char c : s.toCharArray()) {
            int idx = c - 'a';
            if (node.children[idx] == null) {
                node.children[idx] = new TrieNode();
            }
            node = node.children[idx];
        }
        node.endOfWord = true;
    }

    static boolean query(String s) {
        TrieNode node = root;
        for (char c : s.toCharArray()) {
            int idx = c - 'a';
            if (node.children[idx] == null) {
                return false;
            }
            node = node.children[idx];
        }
        return true;
    }

    static class Pair implements Comparable<Pair> {
        int len;
        String s;

        public Pair(int len, String s) {
            this.len = len;
            this.s = s;
        }

        public int compareTo(Pair other) {
            if (this.len != other.len) {
                return Integer.compare(this.len, other.len);
            }
            return this.s.compareTo(other.s);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int n = Integer.parseInt(br.readLine());
        List<Pair> list = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            String s = br.readLine();
            insert(s);
            for (int j = 1; j <= s.length(); j++) {
                for (int k = 0; k <= s.length() - j; k++) {
                    String t = s.substring(k, k + j);
                    if (query(t)) {
                        list.add(new Pair(j, t));
                    }
                }
            }
        }
        Collections.sort(list);
        String ans = list.get(0).s;
        for (int i = 1; i < list.size(); i++) {
            if (list.get(i).len == list.get(i - 1).len) {
                ans = Math.min(ans, list.get(i).s);
            } else {
                break;
            }
        }
        System.out.println(ans);
    }
}

