#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int n, m;
    scanf("%d %d", &n, &m);
    long long x[n], y[m];
    for (int i = 0; i < n; i++) scanf("%lld", &x[i]);
    for (int i = 0; i < m; i++) scanf("%lld", &y[i]);
    long long sum_x[n + 1], sum_y[m + 1];
    for (int i = 0; i < n; i++) sum_x[i + 1] = (sum_x[i] + x[i]) % MOD;
    for (int i = 0; i < m; i++) sum_y[i + 1] = (sum_y[i] + y[i]) % MOD;
    long long ans = 0;
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            long long dx = (x[j] - x[i]) % MOD;
            for (int k = 0; k < m; k++) {
                for (int l = k + 1; l < m; l++) {
                    long long dy = (y[l] - y[k]) % MOD;
                    ans = (ans + (dx * dy) % MOD * (sum_x[j] - sum_x[i]) % MOD * (sum_y[l] - sum_y[k]) % MOD) % MOD;
                }
            }
        }
    }
    printf("%lld\n", ans);
    return 0;
}
