        for (int j = i + 1; j < n; j++) {
            int dx = (x[j] - x[i]) % MOD;
            for (int k = 0; k < m - 1; k++) {
                for (int l = k + 1; l < m; l++) {
                    int dy = (y[l] - y[k]) % MOD;
                    ans = (ans + (long long)dx * dy) % MOD;
                }
            }
        }
