        int maxLen = 0;
        List<String> ans = new ArrayList<>();
        for (int len = 1; len <= 50; len++) {
            for (int i = 0; i < (1 << len); i++) {
                StringBuilder sb = new StringBuilder();
                for (int j = 0; j < len; j++) {
                    if ((i & (1 << j)) != 0) {
                        sb.append((char) ('a' + j));
                    }
                }
                if (query(sb.toString())) {
                    ans.add(sb.toString());
                    maxLen = Math.max(maxLen, sb.length());
                }
            }
