            inv[i] = (inv[i + 1] * (i + 1)) % MOD;
        }

        long ans = 0;
        for (int i = 1; i <= n; i++) {
            long cur = (fact[i - 1] * inv[i - 1]) % MOD;
            long add = (cur * (i + 1)) % MOD;
            ans = (ans + add) % MOD;
        }
        pw.println(ans);
        pw.close();
    }

