void bfs(int s) {
    int i, j;
    memset(dist, -1, sizeof(dist));
    memset(vis, 0, sizeof(vis));
    qe[ql++] = s;
    vis[s] = 1;
    dist[s] = 0;
    while (ql <= qr) {
        int u = qe[ql++];
        for (i = head[u]; i >= 0; i = e[i].x) {
            int v = e[i].y;
            if (!vis[v]) {
                vis[v] = 1;
                dist[v] = dist[u] + 1;
                qe[++qr] = v;
