public class Main {
    static final int N = 100005;
    static int n, m, q;
    static int[] head = new int[N];
    static int[] to = new int[N * 2];
    static int[] nxt = new int[N * 2];
    static int tot;
    static int[] dis = new int[N];
    static int[] col = new int[N];
    static int[] dep = new int[N];
    static int[] fa = new int[N];
    static int[] top = new int[N];
    static int[] siz = new int[N];
    static int[] dfn = new int[N];
    static int[] low = new int[N];
    static int[] stk = new int[N];
    static int[] scc = new int[N];
    static int idx, tim;
    static int[] que = new int[N];
    static int[] val = new int[N];

    static void add(int u, int v) {
        nxt[++tot] = head[u];
        to[head[u] = tot] = v;
    }

    static void dfs(int u, int f) {
        dep[u] = dep[fa[u] = f] + 1;
        for (int i = head[u]; i != 0; i = nxt[i]) {
            int v = to[i];
            if (v == f) continue;
            dfs(v, u);
        }
    }

    static void tarjan(int u) {
        dfn[u] = low[u] = ++tim;
        stk[++idx] = u;
        for (int i = head[u]; i != 0; i = nxt[i]) {
            int v = to[i];
            if (dfn[v] == 0) {
                tarjan(v);
                low[u] = Math.min(low[u], low[v]);
            } else if (scc[v] == 0) {
                low[u] = Math.min(low[u], dfn[v]);
            }
        }
        if (dfn[u] == low[u]) {
            int v;
            do {
                v = stk[idx--];
                scc[v] = val[u];
            } while (v != u);
        }
    }

    static void bfs(int s) {
        Arrays.fill(dis, 0x3f3f3f3f);
        Queue<Integer> q = new LinkedList<>();
        q.add(s);
        dis[s] = 0;
        while (!q.isEmpty()) {
            int u = q.poll();
            for (int i = head[u]; i != 0; i = nxt[i]) {
                int v = to[i];
                if (dis[v] == 0x3f3f3f3f) {
                    dis[v] = dis[u] + 1;
                    q.add(v);
                }
            }
        }
    }

    static void solve() throws IOException {
        n = readInt();
        m = readInt();
        for (int i = 1; i <= m; i++) {
            int u = readInt();
            int v = readInt();
            add(u, v);
            add(v, u);
        }
        q = readInt();
        for (int i = 1; i <= q; i++) {
            int u = readInt();
            int d = readInt();
            int c = readInt();
            val[i] = c;
            bfs(u);
            for (int j = 1; j <= n; j++) {
                if (dis[j] <= d) {
                    que[j] = i;
                }
            }
            Arrays.fill(dis, 0x3f3f3f3f);
        }
        for (int i = 1; i <= n; i++) {
            if (que[i] != 0) {
                dfs(i, 0);
                for (int j = 1; j <= n; j++) {
                    if (scc[j] == 0) {
                        scc[j] = scc[fa[j]];
                    }
                }
                for (int j = 1; j <= n; j++) {
                    if (scc[j] == scc[i]) {
                        col[j] = val[que[j]];
                    }
                }
                Arrays.fill(scc, 0);
            }
        }
        for (int i = 1; i <= n; i++) {
            out.println(col[i]);
        }
    }

    static BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
    static PrintWriter out = new PrintWriter(System.out);
    static StringTokenizer st = new StringTokenizer("");

    static String next() throws IOException {
        while (!st.hasMoreTokens()) {
            st = new StringTokenizer(br.readLine());
        }
        return st.nextToken();
    }

    static int readInt() throws IOException {
        return Integer.parseInt(next());
    }

    public static void main(String[] args) throws IOException {
        solve();
        out.close();
    }
