#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

struct DSU {
    vector<int> parent, size;

    DSU(int n) : parent(n), size(n, 1) {
        iota(parent.begin(), parent.end(), 0);
    }

    int find(int x) {
        if (parent[x] != x) {
            parent[x] = find(parent[x]);
        }
        return parent[x];
    }

    void merge(int x, int y) {
        x = find(x);
        y = find(y);
        if (x != y) {
            parent[y] = x;
            size[x] += size[y];
            size[y] = 0;
        }
    }
};

int main() {
    int n, x, y;
    cin >> n >> x >> y;

    vector<int> c(n), w(n);
    for (int i = 0; i < n; i++) {
        cin >> c[i] >> w[i];
    }

    vector<vector<int>> cnt(n + 1);
    for (int i = 0; i < n; i++) {
        cnt[c[i]].push_back(w[i]);
    }

    for (int i = 1; i <= n; i++) {
        sort(cnt[i].begin(), cnt[i].end());
    }

    vector<int> pref(n + 1);
    for (int i = 1; i <= n; i++) {
        pref[i] = pref[i - 1] + cnt[i].size();
    }

    vector<vector<int>> dp(n + 1, vector<int>(n + 1));
    for (int i = 0; i <= n; i++) {
        dp[i][0] = 1;
    }

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= i; j++) {
            int sum = 0;
            for (int k = 0; k < cnt[i].size(); k++) {
                sum += cnt[i][k];
                if (sum <= x) {
                    dp[i][j] = (dp[i][j] + dp[i - 1][j - 1]) % MOD;
                }
                if (j > 1 && sum <= y) {
                    dp[i][j] = (dp[i][j] + dp[i - 1][j - 2]) % MOD;
                }
            }
        }
    }

    long long ans = 0;
    for (int i = 1; i <= n; i++) {
        ans = (ans + dp[n][i]) % MOD;
    }

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            if (i != j) {
                int sum = 0;
                for (int k = 0; k < cnt[i].size(); k++) {
                    sum += cnt[i][k];
                    if (sum <= x) {
                        ans = (ans + dp[n - cnt[j].size()][i - 1]) % MOD;
                    }
                    if (j > 1 && sum <= y) {
                        ans = (ans + dp[n - cnt[j].size()][i - 2]) % MOD;
                    }
                }
            }
        }
    }

    cout << ans << endl;

    return 0;
