    for (int i = 1; i <= n; i++) scanf("%d", &x[i]);
    for (int i = 1; i <= n; i++) {
        l[i] = max(l[i - 1], x[i] - x[i - 1]);
        r[i] = max(r[i - 1], x[i + 1] - x[i]);
    }
    for (int i = 1; i <= n; i++) {
        if (l[i] <= v) {
            f[i] = 1;
            for (int j = 1; j < i; j++) {
                if (f[j] && r[j] <= v) {
                    f[i] = 0;
                    break;
                }
            }
        }
