    for (int i = 1; i <= n; i++) {
        if (f[i]) {
            g[i] = 1;
            for (int j = 1; j < i; j++) {
                if (g[j] && l[i] <= v) {
                    g[i] = 0;
                    break;
                }
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        if (f[i] && g[i]) printf("Possible\n");
