```java
import java.io.*;
import java.util.*;

public class Main {
    static final int N = (int) 2e5 + 5;
    static int n, V;
    static int[] x = new int[N];
    static boolean[] ans = new boolean[N];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        n = Integer.parseInt(st.nextToken());
        V = Integer.parseInt(st.nextToken());
        st = new StringTokenizer(br.readLine());
        for (int i = 1; i <= n; i++) {
            x[i] = Integer.parseInt(st.nextToken());
        }
        for (int i = 1; i <= n; i++) {
            ans[i] = check(i);
        }
        for (int i = 1; i <= n; i++) {
            System.out.println(ans[i] ? "Possible" : "Impossible");
        }
    }

    static boolean check(int x) {
        int l = 1, r = n;
        while (l < r) {
            int mid = (l + r + 1) >> 1;
            if (check(x, mid)) l = mid;
            else r = mid - 1;
        }
        return check(x, l);
    }

    static boolean check(int x, int k) {
        int v = V;
        for (int i = k + 1; i <= n; i++) {
            if (x[i] - x[i - 1] > v) return false;
            v -= x[i] - x[i - 1];
            if (v <= 0) return false;
            v >>= 1;
        }
        for (int i = k; i >= 1; i--) {
            if (x[i + 1] - x[i] > v) return false;
            v -= x[i + 1] - x[i];
            if (v <= 0) return false;
            v >>= 1;
        }
        return true;
    }
}
