        }
        int[][] dp = new int[n + 1][K + 1];
        int[][] dp2 = new int[n + 1][K + 1];
        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= K; j++) {
                dp[i][j] = MAX;
                dp2[i][j] = MAX;
            }
        }
        dp[0][0] = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= K; j++) {
                dp2[i + 1][j] = Math.min(dp2[i + 1][j], dp[i][j]);
                if (j + a[i] <= K) {
                    dp2[i + 1][j + a[i]] = Math.min(dp2[i + 1][j + a[i]], dp[i][j] + a[i]);
                }
            }
            for (int j = 0; j <= K; j++) {
                dp[i + 1][j] = Math.min(dp[i + 1][j], dp2[i][j]);
            }
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            boolean flag = false;
            for (int j = 0; j <= K; j++) {
                if (dp[n][j] > a[i] && dp[n - 1][j] <= a[i]) {
                    flag = true;
                    break;
                }
            }
            if (!flag) {
                ans++;
            }
        }
        System.out.println(ans);
    }
}
