    }
    long long int cost = 0;
    for (int i = 0; i < n; i++) {
        long long int min = l[i];
        long long int max = r[i];
        for (int j = 0; j < i; j++) {
            if (r[j] >= l[i] && r[j] <= max) {
                min = l[i];
                max = r[j];
                break;
            } else if (r[j] < l[i]) {
                continue;
            } else {
                long long int temp = (l[i] + r[j] - 1) / 2;
                if (temp > max) {
                    max = temp;
                }
            }
        }
        cost += (max - min + 1) * (max - min + 1);
    }
    printf("%lld\n", cost);
    return 0;
}
