        int n = Integer.parseInt(br.readLine());
        int[] l = new int[n];
        int[] r = new int[n];
        StringTokenizer st;
        for (int i = 0; i < n; i++) {
            st = new StringTokenizer(br.readLine());
            l[i] = Integer.parseInt(st.nextToken());
            r[i] = Integer.parseInt(st.nextToken());
        }
        int[] pos = new int[n];
        for (int i = 0; i < n; i++) {
            pos[i] = i;
        }
        Arrays.sort(pos, (a, b) -> (l[a] - l[b]));
        long[] prefix = new long[n];
        prefix[0] = l[pos[0]];
        for (int i = 1; i < n; i++) {
            prefix[i] = prefix[i - 1] + (l[pos[i]] - l[pos[i - 1]]);
        }
        long ans = 0;
        for (int i = 0; i < n; i++) {
            int left = Math.max(0, l[i] - (r[pos[0]] - r[i]));
            int right = Math.min(n - 1, r[i] - l[pos[n - 1]]);
            if (left <= right) {
                ans += (long) left * (r[pos[0]] - r[i]);
                for (int j = 0; j < n; j++) {
                    if (j != i) {
                        int idx = Arrays.binarySearch(pos, j);
                        if (idx < 0) idx = -idx - 1;
                        if (idx > 0) {
                            ans += (long) (r[i] - l[pos[idx - 1]]) * (r[pos[idx - 1]] - r[pos[idx]]);
                        }
                        if (idx < n - 1) {
                            ans += (long) (r[pos[idx + 1]] - r[i]) * (r[pos[idx + 1]] - r[pos[idx]]);
                        }
                    }
                }
                break;
            }
        }
        System.out.println(ans);
    }
}
