using namespace std;

int main() {
    int A, B;
    cin >> A >> B;

    if (A > B) {
        cout << "Impossible" << endl;
        return 0;
    }

    vector<int> honest;
    vector<int> unkind;

    for (int i = 0; i < B; i++) {
        unkind.push_back(i);
    }

    for (int i = 0; i < A; i++) {
        honest.push_back(i);
    }

    int n = B;

    while (honest.size() < n) {
        int a = honest.back();
        int b = unkind.back();
        honest.pop_back();
        unkind.pop_back();

        cout << "? " << a << " " << b << endl;
        string ans;
        cin >> ans;

        if (ans == "Y") {
            honest.push_back(b);
        } else {
            int c = unkind.back();
            unkind.pop_back();
            cout << "? " << a << " " << c << endl;
            string ans2;
            cin >> ans2;

            if (ans2 == "Y") {
                honest.push_back(c);
            } else {
                cout << "? " << b << " " << c << endl;
                string ans3;
                cin >> ans3;

                if (ans3 == "Y") {
                    honest.push_back(c);
                } else {
                    honest.push_back(b);
                }
            }
        }
    }

    string result(n, '0');
    for (int i : honest) {
        result[i] = '1';
    }

    cout << "! " << result << endl;
    return 0;
}
