import sys

def ask(a, b):
    print(f'? {a} {b}')
    sys.stdout.flush()
    ans = input()
    return ans == 'Y'

def solve():
    A, B = map(int, input().split())
    N = A + B
    if A == 1 and B == 1:
        print('Impossible')
        sys.exit(0)
    ans = [0] * N
    for i in range(N):
        if i == 0:
            ans[i] = 1
        else:
            for j in range(N):
                if j != i and not ans[j]:
                    if ask(i, j):
                        ans[i] = 1
                    else:
                        ans[j] = 1
    print('! ', end='')
    sys.stdout.flush()
    for i in range(N):
        print(ans[i], end='')
    print()
    sys.stdout.flush()

if __name__ == '__main__':
    solve()
