#include <stdio.h>
#include <stdlib.h>
#include <math.h>

int main() {
    int N;
    long long C, K;
    scanf("%d %lld %lld", &N, &C, &K);

    long long T[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld", &T[i]);
    }

    long long ans = 0;
    long long cur = 0;
    for (int i = 0; i < N; i++) {
        cur += (T[i] - (i ? T[i - 1] : 0) + K - 1) / K;
        if (cur > C) {
            ans = (ans == 0) ? cur / C : (ans + (cur - 1) / C);
            cur = (cur - 1) % C + 1;
        }
    }

    printf("%lld\n", ans);
    return 0;
}
