            c.erase(std::unique(c.begin(), c.end()), c.end());
            for (int k = i; k < j; ++k) {
                b[k] = c[0];
            }
        }
    }
    std::sort(b.begin(), b.end());
    b.erase(std::unique(b.begin(), b.end()), b.end());
    std::cout << b.size() << std::endl;
    return 0;
