public class Main {
    static int N = 100000;
    static int M = 200000;
    static int N2 = 100000000;
    static int M2 = 40000000;
    static int[] fa = new int[N2];
    static int[] sz = new int[N2];
    static int[] u = new int[M2];
    static int[] v = new int[M2];
    static int[] uu = new int[M2];
    static int[] vv = new int[M2];
    static int[] id = new int[N2];
    static int[] id2 = new int[N2];
    static int[] cnt = new int[N2];
    static int[] cnt2 = new int[N2];
    static int[] e = new int[M2];
    static int[] ne = new int[M2];
    static int[] h = new int[N2];
    static int idx = 0;
    static int n, m;

    static int find(int x) {
        return fa[x] == x ? x : (fa[x] = find(fa[x]));
    }

    static void dfs(int u) {
        for (int i = h[u]; i != -1; i = e[i]) {
            int j = ne[i];
            if (id2[j] == 0) {
                id2[j] = id[u];
                dfs(j);
            }
        }
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        PrintWriter pw = new PrintWriter(System.out);
        n = sc.nextInt();
        m = sc.nextInt();
        for (int i = 1; i <= m; i++) {
            u[i] = sc.nextInt();
            v[i] = sc.nextInt();
            uu[i] = u[i];
            vv[i] = v[i];
            if (u[i] > v[i]) {
                int temp = u[i];
                u[i] = v[i];
                v[i] = temp;
            }
        }
        Arrays.sort(uu, 1, m + 1);
        Arrays.sort(vv, 1, m + 1);
        int p = 1;
        for (int i = 1; i <= m; i++) {
            if (uu[i] != uu[i - 1]) {
                u[p] = uu[i];
                p++;
            }
        }
        m = p - 1;
        p = 1;
        for (int i = 1; i <= m; i++) {
            if (vv[i] != vv[i - 1]) {
                v[p] = vv[i];
                p++;
            }
        }
        m = p - 1;
        for (int i = 1; i <= n; i++) {
            fa[i] = i;
            sz[i] = 1;
            id[i] = i;
            h[i] = -1;
        }
        for (int i = 1; i <= m; i++) {
            int x = find(u[i]);
            int y = find(v[i]);
            if (x != y) {
                fa[y] = x;
                sz[x] += sz[y];
                cnt[x] += cnt[y];
                id[x] = id[y];
                e[idx] = h[x];
                ne[idx] = y;
                h[x] = idx++;
            }
        }
        for (int i = 1; i <= n; i++) {
            if (id[i] == i) {
                cnt2[id[i]] = cnt[i];
                dfs(i);
            }
        }
        int ans = 0;
        for (int i = 1; i <= n; i++) {
            ans += cnt2[i];
        }
        pw.println(n * n - ans);
        pw.close();
    }

    static class Scanner {
        BufferedReader br;
        StringTokenizer st;

        public Scanner(InputStream in) {
            br = new BufferedReader(new InputStreamReader(in));
        }

        public String next() throws IOException {
            while (st == null || !st.hasMoreTokens()) {
                st = new StringTokenizer(br.readLine());
            }
            return st.nextToken();
